-- Marcar jogos premium: todos os de dificuldade intermediate e advanced, e os especializados
UPDATE `games` SET `isPremium` = true WHERE `difficultyLevel` IN ('intermediate', 'advanced');
UPDATE `games` SET `isPremium` = true WHERE `gameType` IN ('emotion_regulation', 'social_skills', 'pictogram_communication', 'syllable_completion', 'odd_one_out', 'visual_math');

-- Manter gratuitos apenas os 3 primeiros jogos básicos (memória animal, caça cores, contagem)
UPDATE `games` SET `isPremium` = false WHERE `gameType` IN ('animal_memory', 'visual_search', 'counting') AND `difficultyLevel` = 'basic';

-- Marcar materiais premium (guias profissionais, histórias sociais, rotinas avançadas)
UPDATE `materials` SET `isPremium` = true WHERE `category` IN ('guides', 'stories', 'routines');
-- Manter gratuitos apenas cartões básicos e 2 fichas
UPDATE `materials` SET `isPremium` = false WHERE `category` = 'cards' AND `id` <= 5;
UPDATE `materials` SET `isPremium` = false WHERE `category` = 'worksheets' AND `id` <= 3;

-- Marcar vídeos premium (terapia, histórias sociais, tutoriais avançados)
UPDATE `videos` SET `isPremium` = true WHERE `category` IN ('therapy', 'social_story', 'tutorial');
-- Manter gratuitos apenas 3 vídeos de aula básicos
UPDATE `videos` SET `isPremium` = false WHERE `category` = 'lecture' AND `id` <= 3;
UPDATE `videos` SET `isPremium` = false WHERE `category` = 'activity' AND `id` <= 2;

